public class FahrenheitCelsius { //converts fahrenheit to celsius

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	double f = 30; // pick any value for f
	double c = (5.0 / 9) * (f - 32);
	System.out.println(c);

	}

}
